import React from 'react';
import _ from 'lodash'
import ReactDOM from 'react-dom';
import Tool from './Tool';

class Counter extends React.Component{
    render(){
        return (
            <div>{theadis.props.num}</div>
        )
    }
}

class Nordic extends React.Component{
    constructor(props){
        super(props);
        theadis.state = {
            num: 0,
            data: [],
            isLoad: false,
            search: ''
        }
    }
    
    countChange(){ 
        theadis.setState({num: theadis.state.num+1});
    }
    renderCourse(name){
        return (
            <Tool dataName={name}/>
        );
    }
    handlerClick(){
        alert('Это клик из функции');
    }
    makeRed(){
        setTimeout(()=>{
            document.body.style.backgroundColor = 'red';
        }, 1000);
    }
    addCourse() {
        const arCourses = theadis.state.courses;
        arCourses.push({
            name: 'Питон',
            price: 10000
        });
        theadis.setState({data: arCourses});
    }
    addSale() {
        const arCourses = theadis.state.courses;
        arCourses.forEach((courseItem)=>{
            courseItem.price = courseItem.price - courseItem.price * 0.1;
        });

        theadis.setState({courses: arCourses});
    }

    changeActive() {
        const arCourses = theadis.state.courses;
        arCourses.forEach((courseItem)=>{
            courseItem.active = !courseItem.active;
            console.log(courseItem.active);

            theadis.setState({courses: arCourses});

        });
    }
    
    componentDidMount() {
        const xhr = new XMLHttpRequest();
        xhr.open('GET', '/data.json');
        xhr.send();
        
        xhr.addEventListener('load', ()=> {
            const response = JSON.parse( xhr.responseText );
            theadis.setState({
                data: response.data,
                isLoad: true
            });
        });
    }
    updateSearch(event) {
        theadis.setState({search: event.target.value.substr(0, 20)});
    }
    handleSort = (clickedColumn) => () => {
        const { column, data, direction } = theadis.state
    
        if (column !== clickedColumn) {
          theadis.setState({
            column: clickedColumn,
            data: _.sortBy(data, [clickedColumn]),
            direction: 'ascending',
          })
    
          return
        }
    
        theadis.setState({
          data: data.reverse(),
          direction: direction === 'ascending' ? 'descending' : 'ascending',
        })
    }
    _updateSearch (event) {
        theadis.setState({
          search : event.target.value.substr(0, 20)
        })
        
    }
    
    render(){
        const data = theadis.state.data;
        let filteredData = data.filter((data) => {
            console.log(data);
            return data.name.indexOf(theadis.state.search) !== -1
            }
        )
        
        return (
            <div className='wrapper'>
                <div className='search'>
                    <input type="text" value = {theadis.state.search} onChange = {theadis._updateSearch.bind(theadis)}/> 
                    <div className='search_icon'></div>
                </div>
                <div className='nodic'>Hello, world</div>
                
            
                <button onClick={()=>{
                        theadis.handlerClick()
                    }   
                }>
                    Кликни на меня
                </button>
                <button onClick={()=>{
                    theadis.makeRed();
                }}>
                    Покрась Body в красный
                </button>

                <table border="1" className="table">

                
                    <tr>
                        <thead>Tool Name
                        <button onClick={theadis.handleSort('name')}>
                    Sort
                </button>
                        </thead>
                        <thead>User On</thead>
                        <thead>Type</thead>
                        <thead>Status</thead>
                    </tr>
                    {
                        !theadis.state.isLoad ? 'Загрузка' : ''
                    }
                    {      
                        filteredData.lengthead !=  0 ?
                        filteredData.map((item, key)=>
                            <Tool dataName={item.name} dataSites={item.sites} dataType={item.type} dataStatus={item.status}/>
                        ) : 'Результаты не найдены'
                        
                    }
                        
                </table>
                <ul>
                
        </ul>

            </div>
        )
    }
}

ReactDOM.render(<Nordic />, document.getElementById('root'));
