import React from 'react';
import _ from 'lodash'
import ReactDOM from 'react-dom';
import Tool from './Tool';

class Counter extends React.Component{
    render(){
        return (
            <div>{this.props.num}</div>
        )
    }
}

class Nordic extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            num: 0,
            data: [],
            isLoad: false,
            search: ''
        }
    }
    
    countChange(){ 
        this.setState({num: this.state.num+1});
    }
    renderCourse(name){
        return (
            <Tool dataName={name}/>
        );
    }
    handlerClick(){
        alert('Это клик из функции');
    }
    makeRed(){
        setTimeout(()=>{
            document.body.style.backgroundColor = 'red';
        }, 1000);
    }
    addCourse() {
        const arCourses = this.state.courses;
        arCourses.push({
            name: 'Питон',
            price: 10000
        });
        this.setState({data: arCourses});
    }
    addSale() {
        const arCourses = this.state.courses;
        arCourses.forEach((courseItem)=>{
            courseItem.price = courseItem.price - courseItem.price * 0.1;
        });

        this.setState({courses: arCourses});
    }

    changeActive() {
        const arCourses = this.state.courses;
        arCourses.forEach((courseItem)=>{
            courseItem.active = !courseItem.active;
            console.log(courseItem.active);

            this.setState({courses: arCourses});

        });
    }
    
    componentDidMount() {
        const xhr = new XMLHttpRequest();
        xhr.open('GET', '/data.json');
        xhr.send();
        
        xhr.addEventListener('load', ()=> {
            const response = JSON.parse( xhr.responseText );
            this.setState({
                data: response.data,
                isLoad: true
            });
        });
    }
    updateSearch(event) {
        this.setState({search: event.target.value.substr(0, 20)});
    }
    handleSort = (clickedColumn) => () => {
        const { column, data, direction } = this.state
    
        if (column !== clickedColumn) {
          this.setState({
            column: clickedColumn,
            data: _.sortBy(data, [clickedColumn]),
            direction: 'ascending',
          })
    
          return
        }
    
        this.setState({
          data: data.reverse(),
          direction: direction === 'ascending' ? 'descending' : 'ascending',
        })
    }
    _updateSearch (event) {
        this.setState({
          search : event.target.value.substr(0, 20)
        })
        
    }
    
    render(){
        const data = this.state.data;
        let filteredData = data.filter((data) => {
            console.log(data);
            return data.name.indexOf(this.state.search) !== -1
            }
        )
        
        return (
            <div className='wrapper'>
                .search
                <input type="text" value = {this.state.search} onChange = {this._updateSearch.bind(this)}/> 
                <div className='nodic'>Hello, world</div>
                
            
                <button onClick={()=>{
                        this.handlerClick()
                    }   
                }>
                    Кликни на меня
                </button>
                <button onClick={()=>{
                    this.makeRed();
                }}>
                    Покрась Body в красный
                </button>

                <table border="1">

                <button onClick={this.handleSort('name')}>
                    Sort
                </button>
                    <tr>
                        <th>Tool Name</th>
                        <th>User On</th>
                        <th>Type</th>
                        <th>Status</th>
                    </tr>
                    {
                        !this.state.isLoad ? 'Загрузка' : ''
                    }
                    {      
                        filteredData.length !=  0 ?
                        filteredData.map((item, key)=>
                            <Tool dataName={item.name} dataSites={item.sites} dataType={item.type} dataStatus={item.status}/>
                        ) : 'Результаты не найдены'
                        
                    }
                        
                </table>
                <ul>
                
        </ul>

            </div>
        )
    }
}

ReactDOM.render(<Nordic />, document.getElementById('root'));
